// src/components/Home.js
import React, { useState } from 'react';
import './css/Home.css'; // Optional: Create a separate CSS file for Home styles

export default function Home() {
  const [symptoms, setSymptoms] = useState('');
  const [history, setHistory] = useState('');
  const [diagnosis, setDiagnosis] = useState('');
  const [report, setReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:8001/assign-triage-level/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ symptoms, history, diagnosis }),
      });

      if (!response.ok) {
        throw new Error('Failed to fetch triage report');
      }

      const data = await response.json();
      setReport(data);
    } catch (error) {
      setError('Error fetching triage report. Please try again.');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home-container">
      <div className="home-header">
        <h1>Welcome to Patient Triage Dignostic System</h1>
      </div>
      <div className="home-content">
        {/* <h2>Home</h2> */}
        <p>Add the Patient Details below:</p>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="symptoms">Symptoms</label>
            <input 
              type="symptoms" 
              id="symptoms" 
              placeholder="Enter Symptoms"
              value={symptoms}
              onChange={(e) => setSymptoms(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="history">History</label>
            <input 
              type="history" 
              id="history" 
              placeholder="Enter Medical History"
              value={history}
              onChange={(e) => setHistory(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="diagnosis">Diagnosis</label>
            <input 
              type="diagnosis" 
              id="diagnosis" 
              placeholder="Enter Preliminary Diagnosis" 
              value={diagnosis} 
              onChange={(e) => setDiagnosis(e.target.value)}
            />
          </div>
          <button className="button">TRIAGE LEVEL
            {loading ? 'Processing...' : 'TRIAGE LEVEL'}
          </button>
        </form>
        <p className="footer">
          {/* <a href="/forgot-password" className="link">Forgot Password?</a> */}
        </p>
      </div>
    </div>
  );
}
